// AdoptionForm.js
import React, { useState } from 'react';
import axios from 'axios';

const AdoptionForm = ({ dog }) => {
  const [formData, setFormData] = useState({
    name: '',
    lastName: '',
    dni: '',
    phoneNumber: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/dogs/adopt', {
        dogId: dog._id,  // Assuming dog._id is available from props
        ...formData
      });
      console.log(response.data);
      // Aquí podrías manejar cualquier redirección o mensaje de éxito
    } catch (error) {
      console.error('Error en la adopción:', error.response.data.message);
      // Aquí podrías manejar errores en el formulario
    }
  };

  return (
    <div className="adoption-form">
      <h2>Formulario de Adopción para {dog.name}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre:</label>
          <input type="text" name="name" value={formData.name} onChange={handleInputChange} />
        </div>
        <div>
          <label>Apellido:</label>
          <input type="text" name="lastName" value={formData.lastName} onChange={handleInputChange} />
        </div>
        <div>
          <label>DNI:</label>
          <input type="text" name="dni" value={formData.dni} onChange={handleInputChange} />
        </div>
        <div>
          <label>Número de Teléfono:</label>
          <input type="text" name="phoneNumber" value={formData.phoneNumber} onChange={handleInputChange} />
        </div>
        <button type="submit">Enviar</button>
      </form>
    </div>
  );
};

export default AdoptionForm;
