// LoginForm.js

import React, { useState } from 'react';
import axios from 'axios';

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/login', { username, password });
      console.log(response.data);
      // Guarda el token de sesión en el almacenamiento local del navegador
      localStorage.setItem('token', response.data.token);
      // Aquí podrías redirigir al usuario a la página principal de la aplicación
    } catch (error) {
      console.error('Error en el inicio de sesión:', error.response.data.message);
      setError(error.response.data.message);
    }
  };

  return (
    <div>
      <h2>Iniciar Sesión</h2>
      {error && <div>{error}</div>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre de usuario:</label>
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
        </div>
        <div>
          <label>Contraseña:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <button type="submit">Iniciar Sesión</button>
      </form>
    </div>
  );
};

export default LoginForm;
