import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DogCard from './DogCard';
import DogForm from './AdoptionForm';

const DogList = () => {
  const [dogs, setDogs] = useState([]);
  const [selectedDog, setSelectedDog] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDogs = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/dogs');
        console.log('Response data:', response.data); // Logging response data for debugging
        setDogs(response.data);
        setError(null);
      } catch (error) {
         if (error.request) {
          console.error('No response received:', error.request);
          setError('No response received from the server');
        } else {
          console.error('Error setting up the request:', error.message);
          setError('Error setting up the request');
        }
      }
    };
    fetchDogs();
  }, []);

  const handleAdoption = (dogId) => {
    setDogs((prevDogs) => prevDogs.filter((dog) => dog._id !== dogId));
    setSelectedDog(null);
  };

  const handleSelect = (dog) => {
    setSelectedDog(dog);
  };

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="dog-list">
      {selectedDog ? (
        <DogForm dog={selectedDog} onAdopted={handleAdoption} />
      ) : (
        dogs.map((dog) => (
          <DogCard key={dog._id} dog={dog} onSelect={handleSelect} />
        ))
      )}
    </div>
  );
};

export default DogList;
