import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DogCard from './DogCard';
import DogForm from './AdoptionForm';

const DogList = () => {
  const [dogs, setDogs] = useState([]);
  const [selectedDog, setSelectedDog] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDogs = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/dogs');
        setDogs(response.data);
        setError(null);
      } catch (error) {
        if (error.response) {
          // El servidor respondió con un estado de error
          console.error('Server error response:', error.response.data);
          setError(`Error: ${error.response.status} ${error.response.statusText}`);
        } else if (error.request) {
          // La solicitud se hizo pero no se recibió respuesta
          console.error('No response received:', error.request);
          setError('No se recibió respuesta del servidor');
        } else {
          // Otro tipo de error
          console.error('Error details:', error.message);
          setError('Error al intentar realizar la solicitud');
        }
      }
    };

    fetchDogs();

    // No se necesita ninguna operación de limpieza específica en este caso

  }, []); // El array vacío [] indica que este efecto solo se ejecuta una vez, al montar el componente

  const handleSelect = (dog) => {
    setSelectedDog(dog);
  };

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="dog-list">
      {selectedDog ? (
        <DogForm dog={selectedDog} />
      ) : (
        dogs.map((dog) => (
          <DogCard key={dog._id} dog={dog} onSelect={handleSelect} />
        ))
      )}
    </div>
  );
};

export default DogList;
