import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DogCard from './DogCard';
import DogForm from './AdoptionForm';

const DogList = () => {
  const [dogs, setDogs] = useState([]);
  const [selectedDog, setSelectedDog] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDogs = async () => {
      try {
        const response = await axios.get('/api/dogs');
        setDogs(response.data);
        setError(null); // Reiniciar el estado de error si la solicitud tiene éxito
      } catch (error) {
        console.error('Error al obtener datos de perros:', error);
        setError('Error al obtener datos de perros');
      }
    };
    fetchDogs();
  }, []);

  const handleSelect = (dog) => {
    setSelectedDog(dog);
  };

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="dog-list">
      {selectedDog ? (
        <DogForm dog={selectedDog} />
      ) : (
        dogs.map((dog) => (
          <DogCard key={dog._id} dog={dog} onSelect={handleSelect} />
        ))
      )}
    </div>
  );
};

export default DogList;
